mapper.select:
select t1.*, sum(uvt) as uvs,sum(today_uv) as todayUvSum,sum(today_pv) as todayPvSum from logloan t1 left join (SELECT COUNT(1) as uvt, loanId FROM(SELECT DISTINCT deviceId, mobile, loanId, channel, ip FROM log_visit where 1 = 1
and loanId in (select relId from logloan WHERE id = #{id})
and createDat between #{beginTime} and #{endTime}
and loanId in (select id from loan WHERE title like CONCAT(CONCAT('%',#{relId},'%')))
GROUP BY loanId ) as tmp )as t2 on t2.loanId = t1.relId where 1 = 1
and t1.id = #{id}
and t1.relId in (select id from loan WHERE title like CONCAT(CONCAT('%',#{relId},'%')))
and t1.refer = #{refer}
and t1.createdAt between #{beginTime} and #{endTime}
GROUP BY relId

select sum(pv) as pvNum, sum(uvt) as uvNum,sum(today_uv) as todayUvSum,sum(today_pv) as todayPvSum from logloan t1 left join (SELECT COUNT(1) as uvt, loanId FROM (SELECT DISTINCT deviceId, mobile, loanId, channel, ip FROM log_visit where 1 = 1
and createDat between #{beginTime} and #{endTime}
and loanId in (select id from loan WHERE title like CONCAT(CONCAT('%',#{relId},'%')))
) as tmp GROUP BY loanId ) as t2 on t2.loanId = t1.relId where 1 = 1
and t1.id = #{id}
and t1.relId in (select id from loan WHERE title like CONCAT(CONCAT('%',#{relId},'%')))
and t1.refer = #{refer}
and t1.createdAt between #{beginTime} and #{endTime}
